package com.example.wadendlyimport android.Rimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerView/*** A bridge that tells the recyclerview how to display the data we give it*/class TaskItemAdapter( val listOfItems: List<String>,val longClickListener:onLongClickListener):RecyclerView.Adapter<TaskItemAdapter.ViewHolder>(){    interface onLongClickListener {        fun onItemLongClicked(position: Int)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): TaskItemAdapter.ViewHolder {        val context = parent.context        val inflater = LayoutInflater.from(context)        val contacView = inflater.inflate(android.R.layout.simple_list_item_1, parent, false)        return ViewHolder(contacView)    }    override fun onBindViewHolder(holder: TaskItemAdapter.ViewHolder, position: Int) {        val item = listOfItems.get(position)        holder.textView.text = item    }    override fun getItemCount(): Int {        return listOfItems.size    }    // handle viewHolder    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val textView: TextView        init {            textView = itemView.findViewById(android.R.id.text1)            itemView.setOnLongClickListener {                longClickListener.onItemLongClicked(adapterPosition)                true            }        }    }}